	structstu*head=NULL,*nextp;
	if(p!=NULL){
		head=p;
		while(head->next!=NULL){
			head=head->next;
		}
	}
	printf("顺序输入学号姓名性别（男1女0）年龄成绩输入-1结束\n");
	nextp=(structstu*)malloc(sizeof(structstu));
	nextp->next=NULL;
	scanf("%d",&nextp->num);
	while(nextp->num!=-1){
		scanf("%s%d%d%d",nextp->name,&nextp->sex,&nextp->age,&nextp->score);
		if(head==NULL){
			p=nextp;
			head=p;
		}else{
			head->next=nextp;
			head=head->next;
		}
		nextp=(structstu*)malloc(sizeof(structstu));
		nextp->next=NULL;
		scanf("%d",&nextp->num);
	}
}
//
voidprint(structstu*p){
