	printf("输入学号：");
	intnum;
	scanf("%d",&num);

	while(1){
		if(p->num==num){
			break;
		}
		p=p->next;
		if(p==NULL)
			break;
	}
	if(p!=NULL)
		printf("%d%s%d%d%d\n",p->num,p->name,p->sex,p->age,p->score);
	else
		printf("WrongNumber\n");
}

voidmodify(structstu*p){
	structstu*head;
